//stores a markup object
export abstract class Markup
{
  //eslint-disable-next-line no-useless-escape
	public static MARKUP_REGEX = /\[!!([^\|]+)\|([^_]+)_([^!]+)!!\]/;

  private _displayName: string;
  private _firstName: string;
  private _lastName: string;
  //
  // public static create(displayName: string, firstName: string, lastName: string): void
  // {
  //   this._firstName = firstName;
  //   this._lastName = lastName;
  //   this._displayName = displayName;
  // }

  private constructor(displayName: string, firstName: string, lastName: string)
  {
    this._firstName = firstName;
    this._lastName = lastName;
    this._displayName = displayName;
  }

  public static create(rawString: string): Markup
  {
    const pieces: RegExpExecArray | null = MarkupUtils.getMarkupPieces(rawText);
    if (pieces == null || pieces.length !== 4)
    {
      return null
    }

    const markupClass: Markup = new Markup();

    this._displayName = pieces[1];
    this._firstName = pieces[2];
    this._lastName = pieces[3];

    return this;
  }

  public get firstName(): string
  {
    return this._firstName;
  }

  public get lastName(): string
  {
    return this._lastName;
  }

  public get displayName(): string
  {
    return this._displayName;
  }


}
